ST.xhtml:
(* --- Initialize state. --------------------------------------------------- *)
IF firstRun THEN
    firstRun := FALSE;

    (* Initialize the state variables. *)
    Good_x := 0;
END_IF;

(* --- Process all events. ------------------------------------------------- *)
isProgress := TRUE;
(* Perform events until none can be done anymore. *)
WHILE isProgress DO
    isProgress := FALSE;

    (* Try to perform event "noSync". *)
    eventEnabled := TRUE;
    IF TRUE THEN
        edge_Good_1 := 1;
    ELSE
        eventEnabled := FALSE;
    END_IF;
    IF eventEnabled THEN
        eventEnabled := FALSE;
    END_IF;
    IF eventEnabled THEN
        isProgress := TRUE;
        current_Good_x := Good_x;
        IF edge_Good_1 = 1 THEN
            Good_x := (current_Good_x + 1) MOD 6;
        END_IF;
    END_IF;

    (* Try to perform event "noMonitor". *)
    eventEnabled := TRUE;
    IF TRUE THEN
        edge_Good_1 := 1;
    ELSE
        eventEnabled := FALSE;
    END_IF;
    IF eventEnabled THEN
        isProgress := TRUE;
        current_Good_x_1 := Good_x;
        IF edge_Good_1 = 1 THEN
            Good_x := (current_Good_x_1 + 5) MOD 6;
        END_IF;
    END_IF;

    (* Try to perform event "channel". *)
    eventEnabled := TRUE;
    senderAut := 0;
    IF senderAut = 0 THEN
        eventEnabled := FALSE;
    END_IF;
    IF eventEnabled THEN
        receiverAut := 0;
        IF receiverAut = 0 THEN
            eventEnabled := FALSE;
        END_IF;
    END_IF;
    IF eventEnabled THEN
        eventEnabled := FALSE;
    END_IF;
    IF eventEnabled THEN
        isProgress := TRUE;
    END_IF;
END_WHILE;
