ST.xhtml:
(* --- Initialize state or update continuous variables. -------------------- *)
IF firstRun THEN
    firstRun := FALSE;
    loopsKilled := 0;

    (* Initialize the state variables. *)
    p_x := 5;
    p_t := 0.0;
    (* Reset timer of "p_t". *)
    preset_p_t := p_t;
    ton_p_t(IN := FALSE, PT := preset_p_t);
    ton_p_t(IN := TRUE, PT := preset_p_t);
ELSE
    (* Update remaining time of "p_t". *)
    ton_p_t(IN := TRUE, PT := preset_p_t, Q => timeOut, ET => curValue);
    p_t := SEL(timeOut, preset_p_t - curValue, 0.0);
END_IF;

(* --- Process all events. ------------------------------------------------- *)
isProgress := TRUE;
(* Perform events until none can be done anymore. *)
(* Track the number of iterations and abort if there are too many. *)
loopCount := 0;
WHILE isProgress AND loopCount < 101 DO
    loopCount := loopCount + 1;
    isProgress := FALSE;

    (* Try to perform event "e". *)
    eventEnabled := TRUE;
    IF p_t <= 1.0 AND p_x = 5 THEN
        edge_p__1 := 1;
    ELSE
        eventEnabled := FALSE;
    END_IF;
    IF eventEnabled THEN
        isProgress := TRUE;
        current_p_t := p_t;
        IF edge_p__1 = 1 THEN
            p_t := 7.0;
            (* Reset timer of "p_t". *)
            preset_p_t := p_t;
            ton_p_t(IN := FALSE, PT := preset_p_t);
            ton_p_t(IN := TRUE, PT := preset_p_t);
        END_IF;
    END_IF;
END_WHILE;
(* Register the first 9999 aborted loops. *)
IF loopCount >= 101 THEN
    loopsKilled := MIN(loopsKilled + 1, 9999);
END_IF;
