ST.xhtml:
(* --- Initialize state or update continuous variables. -------------------- *)
IF firstRun THEN
    firstRun := FALSE;

    (* Initialize the state variables. *)
    p_b := TRUE;
    p_x := 0;
    p_y := 1.23;
    p_ve := g_LIT1;
    p_v1 := 0;
    litStruct.field1 := 0.0;
    litStruct.field2 := 0.0;
    p_v2 := litStruct;
    litStruct.field1 := 0.0;
    litStruct.field2 := 0.0;
    litStruct__1.field1 := litStruct;
    litStruct__1.field2 := 0.0;
    p_v3 := litStruct__1;
    litStruct__2.field1 := 0;
    litStruct__2.field2 := 0;
    p_tv := litStruct__2;
    p_j := 0;
    p_r := 1000000.0;
    p_t := 0.0;
    (* Reset timer of "p_t". *)
    preset_p_t := p_t;
    ton_p_t(IN := FALSE, PT := preset_p_t);
    ton_p_t(IN := TRUE, PT := preset_p_t);
    p := p_l1;
ELSE
    (* Update remaining time of "p_t". *)
    ton_p_t(IN := TRUE, PT := preset_p_t, Q => timeOut, ET => curValue);
    p_t := SEL(timeOut, preset_p_t - curValue, 0.0);
END_IF;

(* --- Process all events. ------------------------------------------------- *)
isProgress := TRUE;
(* Perform events until none can be done anymore. *)
WHILE isProgress DO
    isProgress := FALSE;

    (* Try to perform event "p.evt". *)
    eventEnabled := TRUE;
    IF p = p_l1 THEN
        edge_p__1 := 1;
    ELSIF p = p_l6 THEN
        edge_p__1 := 2;
    ELSIF p = p_l7 THEN
        edge_p__1 := 3;
    ELSIF p = p_l8 THEN
        edge_p__1 := 4;
    ELSIF p = p_l9 THEN
        edge_p__1 := 5;
    ELSIF p = p_l10 THEN
        edge_p__1 := 6;
    ELSIF p = p_l11 THEN
        edge_p__1 := 7;
    ELSIF p = p_l12 THEN
        edge_p__1 := 8;
    ELSIF p = p_l13 THEN
        edge_p__1 := 9;
    ELSIF p = p_l14 THEN
        edge_p__1 := 10;
    ELSIF p = p_l15 THEN
        edge_p__1 := 11;
    ELSE
        eventEnabled := FALSE;
    END_IF;
    IF eventEnabled THEN
        isProgress := TRUE;
        current_p := p;
        current_p_b := p_b;
        current_p_j := p_j;
        current_p_r := p_r;
        current_p_tv := p_tv;
        current_p_v3 := p_v3;
        current_p_ve := p_ve;
        current_p_x := p_x;
        IF edge_p__1 = 1 THEN
            litStruct__2.field1 := 1;
            litStruct__2.field2 := 2;
            p_tv := litStruct__2;
            p := p_l6;
        ELSIF edge_p__1 = 2 THEN
            p_x := 3;
            p_j := 4;
            p := p_l7;
        ELSIF edge_p__1 = 3 THEN
            rightValue := current_p_tv;
            p_x := rightValue.field1;
            p_j := rightValue.field2;
            p := p_l8;
        ELSIF edge_p__1 = 4 THEN
            p_tv.field1 := 5;
            p := p_l9;
        ELSIF edge_p__1 = 5 THEN
            p_tv.field2 := 6;
            p_j := 7;
            p := p_l10;
        ELSIF edge_p__1 = 6 THEN
            p_v3.field1.field2 := 7.8;
            p := p_l11;
        ELSIF edge_p__1 = 7 THEN
            litStruct.field1 := 1.2;
            litStruct.field2 := 3.4;
            litStruct__1.field1 := litStruct;
            litStruct__1.field2 := 5.6;
            p_v3 := litStruct__1;
            p := p_l12;
        ELSIF edge_p__1 = 8 THEN
            p_j := current_p_j + 1;
            p := p_l13;
        ELSIF edge_p__1 = 9 THEN
            p_b := FALSE;
            p := p_l14;
        ELSIF edge_p__1 = 10 THEN
            IF NOT current_p_b THEN
                p_ve := g_LIT2;
            END_IF;
            p := p_l15;
        ELSIF edge_p__1 = 11 THEN
            p_r := DINT_TO_LREAL(g_inp) + 10.0;
            p := p_l16;
        END_IF;
    END_IF;
END_WHILE;
